import 'package:flutter/material.dart';
class ProfilePageFromYoutube extends StatefulWidget {
  const ProfilePageFromYoutube({super.key});

  @override
  State<ProfilePageFromYoutube> createState() => _ProfilePageFromYoutubeState();
}

class _ProfilePageFromYoutubeState extends State<ProfilePageFromYoutube> {
  //hight
  final double coverhight = 280;
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: ListView(
        children: [
           Buildtop(),
           ContentOfPage()
        ],
      )
    );
  }

  Widget BuildCoverImage()=>Container(
    color: Colors.grey,
    child: Image.network('data:image/jpeg;base64,',
    width: double.infinity,
    height: coverhight,
    fit: BoxFit.cover,),

  );
  Widget BuildProfile()=>CircleAvatar(
    
    radius: 100,
    backgroundColor: Colors.black45,
    backgroundImage: NetworkImage('data:image/jpeg;base64,'),
  );

  Widget Buildtop() =>Stack(
        clipBehavior: Clip.none,
        alignment: Alignment.center,
        children: [
          Container(
            margin: EdgeInsets.only(bottom: 140),
            child: BuildCoverImage()),
          Positioned(
            top: 150,
            child: BuildProfile(),
          ),
          
        ] 
      );

  Widget ContentOfPage()=>Column(
    
    crossAxisAlignment: CrossAxisAlignment.start,
    children: [
      Center(
        child: Container(
          width: 300,
          child: Column(
            children: [Text("Niranjan TK"),
            Text("Junior Software Developer")],
          ),
        ),
      ),
      Center(
        child: Container(
          width: 400,
          child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text("About"),
            Text("In Flutter, the FutureBuilder Widget is used to create widgets based on the latest snapshot of interaction with a Future.  It is necessary for Future to be obtained earlier either through a change of state or change in dependencies. FutureBuilder is a Widget that will help you to execute some asynchronous function and based on that function's result your UI will update.")
          ],
        )),
      ),
      
    ],
  );
}
